mapper.select:
SELECT 

FROM gene_dm_base_gene a

WHERE a.id = #{id}

SELECT
FROM gene_dm_base_gene a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.sickness LIKE '%'||#{sickness}||'%' '%'+#{sickness}+'%' concat('%',#{sickness},'%')
AND a.gene_logogram LIKE '%'||#{geneLogogram}||'%' '%'+#{geneLogogram}+'%' concat('%',#{geneLogogram},'%')
AND a.gene_loci = #{geneLoci}
AND a.gene_composite LIKE '%'||#{geneComposite}||'%' '%'+#{geneComposite}+'%' concat('%',#{geneComposite},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM gene_dm_base_gene a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gene_dm_base_gene SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
