mapper.select:
SELECT 

FROM gene_dm_loci a

WHERE a.id = #{id}

SELECT
FROM gene_dm_loci a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.loci LIKE '%'||#{loci}||'%' '%'+#{loci}+'%' concat('%',#{loci},'%')
AND a.gene_type LIKE '%'||#{geneType}||'%' '%'+#{geneType}+'%' concat('%',#{geneType},'%')
AND a.tube_id = #{tubeId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM gene_dm_loci a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gene_dm_loci SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
