mapper.select:
SELECT 

FROM gene_dm_user a

WHERE a.id = #{id}

SELECT
FROM gene_dm_user a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_name LIKE '%'||#{userName}||'%' '%'+#{userName}+'%' concat('%',#{userName},'%')
AND a.user_tele = #{userTele}
AND a.tube_id LIKE '%'||#{tubeId}||'%' '%'+#{tubeId}+'%' concat('%',#{tubeId},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM gene_dm_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gene_dm_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
